using System.Text;
using Microsoft.SemanticKernel.AI;

#pragma warning disable IDE0130
// ReSharper disable once CheckNamespace - Using the main namespace
namespace Microsoft.SemanticKernel;
#pragma warning restore IDE0130

/// <summary>
/// This class represents the content of a streaming chunk generated by KernelFunctionsFromMethod.
/// </summary>
public sealed class StreamingMethodContent : StreamingContent
{
    /// <inheritdoc/>
    public override int ChoiceIndex => 0;

    /// <summary>
    /// Content of a streaming chunk result of a KernelFunctionFromMethod.
    /// </summary>
    public object Content { get; }

    /// <inheritdoc/>
    public override byte[] ToByteArray()
    {
        if (this.Content is byte[])
        {
            return (byte[])this.Content;
        }

        // By default if a native value is not Byte[] we output the UTF8 string representation of the value
        return Encoding.UTF8.GetBytes(this.Content?.ToString());
    }

    /// <inheritdoc/>
    public override string ToString()
    {
        return this.Content.ToString();
    }

    /// <summary>
    /// Initializes a new instance of the <see cref="StreamingMethodContent"/> class.
    /// </summary>
    /// <param name="innerContent">Underlying object that represents the chunk content</param>
    public StreamingMethodContent(object innerContent) : base(innerContent)
    {
        this.Content = innerContent;
    }
}
